import React from 'react'
import ModelBody from './ModalBody';
import './index.css'

function TaskModal(props) {
    //console.log(JSON.stringify(props))
    if (typeof props.data === 'undefined') return <div></div> // return empty div if data not set

    return (
        <div className="modal">
            <div className="modal-header">
                <b>{props.data[0].Task_type[0].toUpperCase() + props.data[0].Task_type.substring(1)} </b>Task
                <br></br>
                <b>Title: </b> {props.data[0].Tittle}
                <div onClick={props.closeModal}>x</div>
            </div>






            <ModelBody data={props.data[0]} />

        </div>

    )
}

export default TaskModal
