import React from 'react'
import './Task.css'
import Image from 'react-bootstrap/Image';

function Task(props) {

    return (
        <div className="column" onClick={() => props.handleShow(props.id)}>
            <div className="card">
                <div>
                    <span className="close" id={props.id} onClick={props.handleDelete}>X</span>
                </div>
                {/*<h6>{props.Task_type[0].toUpperCase() + props.Task_type.substring(1)} Task</h6>*/}
                <h6>{props.Task_type}</h6>
                <h3>{props.Tittle}</h3>
                <p>{props.Description}</p>
                {/*<img src={props.Image} style={{ width: '100px', height: '100px' }} />*/}
                <div style={{padding: "15px"}}><Image src={props.data.Image} thumbnail /></div>
                <p>{props.Date}</p>
            </div>
        </div>
    )
}

export default Task
