import React, { useState, useEffect } from 'react'
import axios from 'axios';
import './index.css'
import Search from './Find';

const ContextContainer = React.createContext(null);

function FindTasks(props) {
  console.log(JSON.stringify(props));
  return (
    <div className="modal">
      <div className="modal-header">
        <div>Detail</div>
        <div onClick={props.closeModal}>x</div>
      </div>
      <div>
        <div>Task-Type: {props.currentTask.Task_type}</div>
        <div>Task_Tittle: {props.currentTask.Tittle}</div>
        <div>Task_Description: {props.currentTask.Description}</div>
        <div>Task_Suburb: {props.currentTask.Suburb}</div>
        <div>Task_Date: {props.currentTask.Date}</div>
        <div>Payment_Type: {props.currentTask.Payment_type}</div>
        <div>Budget: {props.currentTask.Budget}</div>
      </div>
    </div>
  )
}
function TaskList(props) {
  const [Task_Date, setDate] = useState('');
  const [Task_Tittle, setTitle] = useState('');
  const [Suburb, setSuburb] = useState('');
  const [tableList, setTableList] = useState([]);
  const [isShowModal, setIsShowModal] = useState(false);
  const [currentTask, setCurrentTask] = useState({});
  const [searchTerm, setSearchTerm] = useState({ criteria: "title", term: "" });

  useEffect(() => {
    axios.get('http://localhost:8000/task').then(response => {
      console.log(response);
      if (response.status === 200) {
        setTableList(response.data)
      }
    })
  }, []);

  function searchTasks(e) {
    //setSearchTerm(e.target.value);
    setSearchTerm({ ...searchTerm, term: e.target.value }); // keep appstate and update
  }
  function setCriteria(e) {
    //console.log('crit: '+e.target.value);
    //setSearchCriteria(e.target.value);
    setSearchTerm({ ...searchTerm, criteria: e.target.value }); // keep appstate and update
  }
  // search task
  const searchHandler = () => {
    axios.post('http://localhost:8000/search_task', { Task_Date, Task_Tittle, Suburb }).then(response => {
      console.log(response);
      if (response.status === 200) {
        setTableList(response.data);
      }
    })
  }

  // delete data
  const deleteRow = (id) => {
    axios.post('http://localhost:8000/delete_task', { id }).then(response => {
      if (response.status === 200) {
        alert(response.data.message);
        if (response.data.code === 0) {
          alert(response.data.message);
          window.location.href = '/';
        }
      }
    })
  }

  const detailsRow = (item) => {
    setCurrentTask(item);
    setIsShowModal(true);
  }

  const closeModal = () => {
    setIsShowModal(false);
  }

  return (
    <div className="task-list">
      {/*<div className="search">
        <input type="text" placeholder="Enter task title" value={Task_Tittle} onChange={(event) => { setTitle(event.target.value) }} className="title-input" />
        <input type="date" value={Task_Date} onChange={(event) => setDate(event.target.value)} />
        <button type="button" onClick={searchHandler}>search</button>
  </div>*/}
      <ContextContainer.Provider value={{ searchTerm, setSearchTerm }}>
        <div style={{ width: "100%" }}>
          <Search ContextContainer={ContextContainer} onChange={searchTasks} setCriteria={setCriteria} />
        </div>
      </ContextContainer.Provider>
      <table className="task-table" cellSpacing="0">
        <thead>
          <tr>
            <th>Task-Type</th>
            <th>Task_Tittle</th>
            <th>Task_Description</th>
            <th>Image</th>
            <th>Task_Suburb</th>
            <th>Task_Date</th>
            <th>Payment_Type</th>
            <th>Budget</th>
            <th>Operation</th>
          </tr>
        </thead>
        <tbody>
          {
            tableList.map((item, index) => {
              return (
                <tr key={index}>
                  <th>{item.Task_type}</th>
                  <th>{item.Tittle}</th>
                  <th>{item.Description}</th>
                  <th>
                    <img src={item.Image} style={{ width: '100px', height: '100px' }} />
                  </th>
                  <th>{item.Suburb}</th>
                  <th>{item.Date}</th>
                  <th>{item.Payment_type}</th>
                  <th>{item.Budget}</th>
                  <th>
                    <button onClick={() => detailsRow(item)} style={{ marginRight: '10px' }}>details</button>
                    <button onClick={() => deleteRow(item._id)}>delete</button>
                  </th>
                </tr>
              )
            })
          }
        </tbody>
      </table>
      {
        isShowModal ? <FindTasks currentTask={currentTask} closeModal={closeModal} /> : null
      }

    </div>
  )
}
export default TaskList;
