import React,{ useContext} from "react";
import TextInput from './TextInput';
import "./Content.css"

function Search(props){
    const { searchTerm } = useContext(props.ContextContainer);

    if(searchTerm.criteria!=='Tittle'){
        return(
            <div className="task-type-div">
            <p className="task-type-p">Search:</p>
                <div>
                    <div key='radio' onChange={props.setCriteria}>
                        <input name="taskType" inline label="Title" type='radio' id='radio' value='Tittle' defaultChecked required />
                        <input name="taskType" inline label="Date" type='radio' id='radio' value='Date' />
                    </div>


            <input labelText="Search Tasks" placeHolder="Enter search criteria" type="Date" controlId="searchText" onChange={props.onChange} defValue="" />
            </div>
            </div>
        );
    }
    else{
        return(
            <div>
            <Row className="mt-2">
                <Col xs="auto">
                <Form.Label>
                    Search:
                </Form.Label>
                </Col>
                <Col xs="auto">
                    <div key='radio' onChange={props.setCriteria}>
                        <Form.Check name="taskType" inline label="Title" type='radio' id='radio' value='Tittle' defaultChecked required />
                        <Form.Check name="taskType" inline label="Date" type='radio' id='radio' value='Date' />
                    </div>
                </Col>
            </Row>
            <TextInput labelText="Search Tasks" placeHolder="Enter search criteria" controlId="searchDate" onChange={props.onChange}  />
            </div>
        );
    }    
}

export default Search;
