import React from 'react'
import {Modal, Button} from 'react-bootstrap';
import ModalBody from './ModalBody';


function TaskModal(props)
{
    //console.log(JSON.stringify(props))
    if(typeof props.data === 'undefined') return <div></div> // return empty div if data not set

    return (
        <div>    
            <Modal show={props.show} onHide={props.handleClose} fullscreen="xl-down">
                <Modal.Header closeButton>
                        <h4>{props.data[0].Task_type[0].toUpperCase() + props.data[0].Task_type.substring(1)}</h4> 
                        <h5>{props.data[0].Tittle}</h5>

                </Modal.Header>
                <Modal.Body>
                    <ModalBody data={props.data[0]}/>                    
                </Modal.Body>
            </Modal> 
      </div>
    )
}

export default TaskModal
