import React ,{useState} from 'react'
import './Task.css'

function Tasks(props) {
    const [currentTask, setCurrentTask] = useState({});
    //console.log(JSON.stringify(props));
    return (
        <div className="column" onClick={() => props.handleShow(props.id)}>
            <div className="card">
                <div>
                    <span className="close" id={props.id} onClick={props.handleDelete}>X</span>
                </div>
                {//<h6>{props.currenTask.Budget[0].toUpperCase() + props.Task_type.substring(1)} Task</h6>
}
                <h3>{props.currenTask.Tittle}</h3>
                <p>{props.currenTask.Description}</p>
                <p>{props.currenTask.Date}</p>
            </div>
        </div>
    )
}

export default Tasks
